import {useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import useHttp from '../../hooks/use-http';
import { getAllComments } from '../../lib/api';
import LoadingSpinner from '../UI/LoadingSpinner';

import classes from './Comments.module.css';
import NewCommentForm from './NewCommentForm';

const Comments = () => {
  const [isAddingComment, setIsAddingComment] = useState(false);
  const {sendRequest,status, data: loadedQuote} = useHttp(getAllComments);
  const params = useParams();

  const { quoteId } = params;

  useEffect(() => {
    sendRequest(quoteId);  
  },[sendRequest,quoteId])

  const startAddCommentHandler = () => {
    setIsAddingComment(true);
  };

  const addCommentHandler = () => {
    sendRequest(quoteId);
  }

  let comments;

  if (status === 'pending') {
    comments = <div className='centered'><LoadingSpinner/></div>
  }

  if (status === 'completed' && loadedQuote.length > 0) {
    comments = <div className='centered'><CommentsList comments={loadedQuote}/></div>
  }

  if (status === 'failed' && loadedQuote.length === 0) {
    comments = <div className='centered'>No comments Found</div>
  }

  return (
    <section className={classes.comments}>
      <h2>User Comments</h2>
      {!isAddingComment && (
        <button className='btn' onClick={startAddCommentHandler}>
          Add a Comment
        </button>
      )}
      {isAddingComment && <NewCommentForm addComment ={addCommentHandler} quoteId={quoteId} />}
      <p>Comments...</p>
    </section>
  );
};

export default Comments;
