import React, {Fragment}from 'react'
import { useRouteMatch } from 'react-router-dom';
import { Link, Route, useParams } from 'react-router-dom';
import Comments from "../components/comments/Comments";
import HighlightedQuote from "../components/quotes/HighlightedQuote";
import useHttp from '../hooks/use-http';
import { getSingleQuote } from '../lib/api';

const Dummy_Quotes = [
  { id: "q1", author: "Himanshu", text: "Learn More Gain More..!!" },
  { id: "q2", author: "Mukko", text: "It's Good to be Selfish..!!" },
];

const QuoteDetails = () => {
  const match = useRouteMatch();
  const param = useParams();

  const { sendRequest, status, data: loadedQuote, error } = useHttp(getSingleQuote);

  const quote = Dummy_Quotes.find(quote => quote.id === param.quoteId);

  if (!quote) {
    return <p>No quote found..!</p>
  }

  return (
    <Fragment>
      <HighlightedQuote text={quote.text} author={quote.author} />
      <Route path={`${match.path}`} exact>
        <div className="centered">
        <Link className="btn--flat" to={`${match.url}/comments`}>Load Comments</Link>
       </div>
      </Route>
      <Route path={`${match.path}/comments`} >
      <Comments />
        </Route>  
    </Fragment>
  )
}

export default QuoteDetails;
